public class Time
{
  public static void main(String[] args)
  {
    String day;
    int month, date, year;
    day = "Friday";
    System.out.println("day = " + day);
    date = 24;
    System.out.println("date = " + date);
    month = 12;
    System.out.println("month = " + month);
    year = 2021;
    System.out.println("year = " + year);
  }
}